import Header from "./components/Header";
import Content from "./components/Content";
import Total from "./components/Total";
import { useEffect, useState } from "react";

function App() {
  const data = [
    { lesson: 1, exercises: 10 },
    { lesson: 2, exercises: 20 },
    { lesson: 3, exercises: 30 },
    { lesson: 4, exercises: 40 },
  ];

  const total = data.map((lesson) => lesson.exercises).reduce((a, b) => a + b);

  return (
    <div>
      <Header title="React course" />
      <Content lesson={data[0].lesson} exercises={data[0].exercises} />
      <Content lesson={data[1].lesson} exercises={data[1].exercises} />
      <Content lesson={data[2].lesson} exercises={data[2].exercises} />
      <Content lesson={data[3].lesson} exercises={data[3].exercises} />

      <Total total={total} />
    </div>
  );
}

export default App;
